mapper.select:
select date_format(concat(date(mytime),' ',floor((hour(mytime)-1)/2)*2+1),'%H')
as time,count(*) as num from time_info where (mytime between #{date1}
and #{date2})and mac_address=#{mac_address}
group by time
order by time

select date_format(concat(date(mytime),' ',floor((hour(mytime)-1)/2)*2+1),'%H')
as time,count(*) as num from time_info where (mytime between #{date1}
and #{date2})and mac_address in (select mac_address from address_info
where city=#{city}
and county=#{county}
and street=#{street})
group by time
order by time

select date_format(concat(date(mytime),' ',floor((hour(mytime)-1)/2)*2+1),'%H')
as time,count(*) as num from time_info where (mytime between #{date1}
and #{date2})and mac_address in (select mac_address from address_info
where city=#{city} and county=#{county}
and street=#{street}
and (specific_address like #{specific_address}"%"))
group by time
order by time

